import React, { useEffect, useState } from "react";
import "ol/ol.css";
import Map from "ol/Map";
import View from "ol/View";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import Feature from "ol/Feature";
import Point from "ol/geom/Point";
import { fromLonLat } from "ol/proj";
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";
import { Icon, Style } from "ol/style";
import Overlay from "ol/Overlay";

// üîπ Firebase REST API URL
const FIREBASE_URL = "https://wellmap-91f48-default-rtdb.firebaseio.com/.json";

const MapComponent = () => {
  const [wells, setWells] = useState([]);

  // üîπ ‰ªé Firebase Ëé∑ÂèñÊï∞ÊçÆ
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(FIREBASE_URL);
        const data = await response.json();

        if (data) {
          // üîπ Firebase ËøîÂõûÁöÑÊòØÂØπË±°ÔºåÈúÄË¶ÅËΩ¨Êç¢ÊàêÊï∞ÁªÑ
          const wellsArray = Object.values(data).map((well, index) => ({
            id: well.id || index, // Á°Æ‰øùÊØè‰∏™ÁÇπÈÉΩÊúâÂîØ‰∏Ä ID
            name: well.name || "Unknown Well",
            lat: well.latitude || 0, // ‰∫ïÁöÑÁ∫¨Â∫¶
            lon: well.longitude || 0, // ‰∫ïÁöÑÁªèÂ∫¶
            details: `
              <strong>Formation:</strong> ${well.stimulated_formation || "N/A"}<br/>
              <strong>Top Depth:</strong> ${well["top_(ft)"] || "N/A"} ft<br/>
              <strong>Bottom Depth:</strong> ${well["bottom_(ft)"] || "N/A"} ft<br/>
              <strong>Stages:</strong> ${well.stimulation_stages || "N/A"}<br/>
              <strong>Volume:</strong> ${well.volume || "N/A"} ${well.volume_units || ""}<br/>
              <strong>Details:</strong> ${well.details || "No details"}
            `,
          }));

          setWells(wellsArray);
        }
      } catch (error) {
        console.error("Ëé∑ÂèñÊï∞ÊçÆÂ§±Ë¥•:", error);
      }
    };

    fetchData();
  }, []);

  // üîπ Âä†ËΩΩ OpenLayers Âú∞Âõæ
  useEffect(() => {
    if (wells.length === 0) return;

    const map = new Map({
      target: "map",
      layers: [
        new TileLayer({
          source: new OSM(),
        }),
      ],
      view: new View({
        center: fromLonLat([-99.1332, 39.8283]), // ÈªòËÆ§ÁæéÂõΩ‰∏≠ÈÉ®
        zoom: 4,
      }),
    });

    // üîπ ÂàõÂª∫Êé®Èíà (markers)
    const features = wells.map((well) => {
      if (!well.lon || !well.lat) return null; // Á°Æ‰øùÁªèÁ∫¨Â∫¶Â≠òÂú®
      const feature = new Feature({
        geometry: new Point(fromLonLat([parseFloat(well.lon), parseFloat(well.lat)])),
        name: well.name,
        details: well.details || "ÊöÇÊó†ËØ¶ÁªÜ‰ø°ÊÅØ",
      });

      feature.setStyle(
        new Style({
          image: new Icon({
            src: "https://openlayers.org/en/latest/examples/data/icon.png",
            scale: 0.05,
          }),
        })
      );

      return feature;
    }).filter(f => f !== null); // ËøáÊª§ÊéâÊó†ÊïàÊï∞ÊçÆ

    // üîπ ÂàõÂª∫ÂõæÂ±Ç
    const vectorLayer = new VectorLayer({
      source: new VectorSource({ features }),
    });

    map.addLayer(vectorLayer);

    // üîπ ÂàõÂª∫ popup
    const container = document.getElementById("popup");
    const content = document.getElementById("popup-content");
    const closer = document.getElementById("popup-closer");

    const overlay = new Overlay({
      element: container,
      autoPan: true,
      autoPanAnimation: { duration: 250 },
    });

    map.addOverlay(overlay);

    closer.onclick = function () {
      overlay.setPosition(undefined);
      closer.blur();
      return false;
    };

    // üîπ ÁõëÂê¨ÁÇπÂáª‰∫ã‰ª∂ÔºåÊòæÁ§∫ popup
    map.on("singleclick", function (evt) {
      const feature = map.forEachFeatureAtPixel(evt.pixel, (feature) => feature);
      if (feature) {
        const coordinates = feature.getGeometry().getCoordinates();
        content.innerHTML = `<h3>${feature.get("name")}</h3><p>${feature.get("details")}</p>`;
        overlay.setPosition(coordinates);
      }
    });

    return () => map.setTarget(undefined);
  }, [wells]);

  return (
    <div>
      <div id="map" style={{ width: "100%", height: "500px" }}></div>
      <div id="popup" className="ol-popup">
        <a href="#" id="popup-closer" className="ol-popup-closer"></a>
        <div id="popup-content"></div>
      </div>
    </div>
  );
};

export default MapComponent;
